mapper.select:
SELECT

FROM users
WHERE 1=1

    AND (
        username LIKE CONCAT('%', #{keyword}, '%')
        OR email LIKE CONCAT('%', #{keyword}, '%')
        OR phone LIKE CONCAT('%', #{keyword}, '%')
    )


    AND parent_id = #{parentId}


    AND DATE(created_at) >= #{dateFrom}


    AND DATE(created_at) <= #{dateTo}

ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT

FROM users
WHERE 1=1

    AND role = #{role}


    AND status = #{status}


    AND parent_id = #{parentId}


    AND (
        username LIKE CONCAT('%', #{keyword}, '%')
        OR email LIKE CONCAT('%', #{keyword}, '%')
        OR phone LIKE CONCAT('%', #{keyword}, '%')
    )


    AND DATE(created_at) >= #{dateFrom}


    AND DATE(created_at) <= #{dateTo}

ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM users
WHERE 1=1

    AND role = #{role}


    AND status = #{status}


    AND parent_id = #{parentId}


    AND (
        username LIKE CONCAT('%', #{keyword}, '%')
        OR email LIKE CONCAT('%', #{keyword}, '%')
        OR phone LIKE CONCAT('%', #{keyword}, '%')
    )


    AND DATE(created_at) >= #{dateFrom}


    AND DATE(created_at) <= #{dateTo}
